import React from 'react';
import { Box, useMediaQuery } from '@mui/material';
import { useEffect, useState } from 'react';
import { useSelector} from "react-redux";
import { useParams} from "react-router-dom";
import Navbar from "../component/Navbar";
import FriendListWidget from "../component/widgets/FriendListWidget";
import MyPostWidget from "../component/widgets/MyPostWidget";
import PostsWidget from "../component/widgets/PostWidget";
import UserWidget from "../component/widgets/UserWidget";

const ProfilePage = () => {

const [user, setUser]= useState(null);
const{userId}= useParams();
const token= useSelector((state) => state.token );
const isNonMobileScreens= useMediaQuery("(min-width: 1000px");


const getUser= async () => {
  const res= await fetch(`http://localhost:3001/users/${userId}`,
  {
    method: "GET",
    headers: {Authorization: `Bearer ${token}`}
  })
  const data= await res.json();
  setUser(data);
}

useEffect(() => {
  getUser();
}, [])


if(!user) return null;



return (
  <Box>
  <Navbar/>
  <Box
    width= "100%"
    padding= "2rem 6%"
    display= {isNonMobileScreens ? "flex" : "block"}
    gap= "2rem"
    justifyContent= "center"
  >
    <Box flexBasis= {isNonMobileScreens ? "26%" : undefined}>
      <UserWidget userId= {_id} picturePath= {picturePath}/>
      <Box m= "2rem 0"/>
    </Box>
    <FriendListWidget userId= {userId}/>
    <Box 
      flexBasis= {isNonMobileScreens ? "42%" : undefined}
      mt= {isNonMobileScreens ? undefined : "2rem"}
    >
      <MyPostWidget picturePath= {userpicturePath}/>
      <PostsWidget userId= {_id}/>
    </Box>   

  </Box>
</Box>
)

}

export default ProfilePage
