import React from 'react';
import { useState } from 'react';
import {
Box,
Button,
TextField,
useMediaQuery,
Typography,
useTheme,
}from "@mui/material";

import EditOutlinedIcon from "@mui/icons-material/EditOutlined";
import { Formik } from "formik";
import * as yup from "yup";
import {  useNavigate} from "react-router-dom";
import { useDispatch } from "react-redux";
import { setLogin } from "../state";
import Dropzone from 'react-dropzone';
import FlexBetween from "./style/FlexBetween";

const registerShema = yup.object().shape({
  firstName: yup.string().required("required"),
  lastName: yup.string().required("required"),
  email: yup.string().email("Invalid Email").required("required"),
  password: yup.string().required("required"),
  location: yup.string().required("required"),
  occupation: yup.string().required("required"),
  picture: yup.string().required("required"),
});

const loginShema = yup.object().shape({
  email: yup.string().email("Invalis  Email").required("required"),
  password: yup.string().required("required"),
});

const initialValuesRegister = {
  firstName: "",
  lastName: "",
  email: "",
  password: "",
  location: "",
  occupation: "",
  picture: "",
};

const initialValuesLogin = {
  email:"",
  password: "",
};


const Form = () => {

  const [ pageType, setPageType ]= useState("login");
  const { palatte } = useTheme();
  const dispatch = useDispatch();
  const navigate = useNavigate;
  const isNonMobile = useMediaQuery("(min-width:600px)");
  const isLogin = pageType === "login";
  const isRegister = pageType === "register";

  const register = async (values, onSubmitProps) => {
    const formData = new FormData();
    for(let value in values){
      formData.append(value, values[value]);
    }

    formData.append.apply("pisturePath", values.picture.name);

    const savedUserResponse = await fetch(
      "https://localhost:3001/auth/register",
      {
        method: "POST",
        body: formData,
      }
    );
    const savedUser = await savedUserResponse.json();
    onSubmitProps.resetForm();

    if(savedUser){
      setPageType("login");
    }
  }


  const login = async(values, onSubmitProps) => {
    const loggedInResponse = await fetch("https://localost:3001/auth/login",{
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify(values),
     });
     const loggedIn = await loggedInResponse.json();
     onSubmitProps.resetForm();

     if(loggedIn){
      dispatch(
        setLogin({
          user: loggedIn.user,
          token: loggedIn.token,
        })
      );
      navigate("/home")
     }
  }


  const handleFormSubmit = async(values, onSubmitProps) => {
    if(isLogin) await login(values, onSubmitProps);
    if(isLogin) await login(values, onSubmitProps);
  }

  return (
    <Formik
      onSubmit={handleFormSubmit}
      initialValues={isLogin ? initialValuesLogin : initialValuesRegister}
      validationSchema={isLogin ? loginShema : registerShema}
    >
      {({
        values,
        errors,
        touched,
        handleBlur,
        handleChange,
        handleSubmit,
        setFieldValue,
        resetForm,
      }) =>(
        <form onSubmit={handleSubmit}>
          <Box 
            display="grid"
            gap="30px"
            gridTemplateColumns="repeat(4, minmax(0, 1fr))"
            sx={{
              "& > div" : {gridColumn: isNonMobile ? undefined : "span 4"},
            }}
          >
            {isRegister && (
              <>
                <TextField
                  label="First Name:"
                  onBlur={handleBlur}
                  onChange={handleChange}
                  name="firstName"
                  error={Boolean(touched.firstName) && Boolean(errors.firstName)}
                  helperText={touched.firstName && errors.firstName}
                  sx= {{gridColumn: "span 4"}}
                />

                <TextField
                  label="Last Name:"
                  onBlur={handleBlur}
                  onChange={handleChange}
                  name="lastName"
                  error={Boolean(touched.lastName) && Boolean(errors.lastName)}
                  helperText={touched.lastName && errors.lastName}
                  sx= {{gridColumn: "span 4"}}
                />

                <TextField
                  label="Location:"
                  onBlur={handleBlur}
                  onChange={handleChange}
                  name="location"
                  error={Boolean(touched.location) && Boolean(errors.location)}
                  helperText={touched.location && errors.location}
                  sx= {{gridColumn: "span 4"}}
                />

                <TextField
                  label="Occupation:"
                  onBlur={handleBlur}
                  onChange={handleChange}
                  name="occupation"
                  error={Boolean(touched.occupation) && Boolean(errors.occupation)}
                  helperText={touched.occupation && errors.occupation}
                  sx= {{gridColumn: "span 4"}}
                />

                <Box
                  gridColumn="span"
                  border={'1px solid ${palette.neutral.medium}'}
                  borderRadius="5px"
                  p="1rem"
                >
                  <Dropzone
                    acceptedFiles=".jpg,.jpeg,.png"
                    multiple={false}
                    onDrop={(acceptedFiles) =>
                      setFieldValue("picture", acceptedFiles[0])
                    }
                  >
                    {({ getRootProps, getInputProps}) => (
                      <Box
                        {...getRootProps()}
                        border= {'2x dashed ${palette.primary.main}'}
                        p= "1rem"
                        sx= {{"&:hover" : { cursor : "pointer"}}}
                      >
                        <input {...getInputProps()} />
                        {!values.picture ? (
                          <p>Add Picture Here</p>
                        ): (
                          <FlexBetween>
                            <Typography>{values.picture.name}</Typography>
                            <EditOutlinedIcon/>
                          </FlexBetween>
                        )}

                      </Box>
                    )}

                  </Dropzone>

                </Box>
              </> 
            )}

            
            <TextField
              label="Email:"
              onBlur={handleBlur}
              onChange={handleChange}
              name="email"
              error={Boolean(touched.email) && Boolean(errors.email)}
              helperText={touched.email && errors.email}
              sx= {{gridColumn: "span 4"}}
            />
            <TextField
              label="Password:"
              type='password'
              onBlur={handleBlur}
              onChange={handleChange}
              name="password"
              error={Boolean(touched.password) && Boolean(errors.password)}
              helperText={touched.password && errors.password}
              sx= {{gridColumn: "span 4"}}
            />

          </Box>

          <Box>
            <Button
              fullWidth
              type='submit'
              sx={{
                m: "2rem 0",
                p: "1rem",
                backgroundColor: palatte.primary.main,
                color: palatte.background.alt,
                "&:hover": {color: palatte.primary.main},
              }}
            >
              {isLogin ? "LOGIN" : "REGISTER"}
            </Button>

            <Typography
              onClick={() => {
                setPageType(isLogin ? "register" : "login")
              }}
              sx={{
                textDecoration: "underline",
                color: palatte.primary.main,
                "&:hover": {
                  cursor: "pointer",
                  color: palatte.primary.light,
                },
              }}
            >
              {isLogin ? "Don't Have an Account? Sign Up" : "Already have an Account? Sign In"}
            </Typography>


          </Box>
        </form>
      )}

    </Formik>
  )
}

export default Form
