import { Box,useMediaQuery} from '@mui/material'
import React, { useState } from 'react'
import Navbar from '../component/Navbar.js'
import UserWidgets from '../component/widgets/UserWidgets.js';
import { useSelector } from 'react-redux';
import MyPostWidget from '../component/widgets/MyPostWidget.js';
import PostsWidget from '../component/widgets/PostsWidget.js';

const HomePage = () => {
  const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");
  const { _id, picturePath} = useSelector((state) => state.user)

  return (
    <Box>
      <Navbar/>
      <Box
        width= "100%"
        padding= "2rem 6%"
        display= {isNonMobileScreens ? "flex" : "block"}
        gap= "0.5rem"
        justifyContent= "space-between"
      >
        <Box flexBasis= {isNonMobileScreens ? "26%" : undefined}>
          <UserWidgets userId= {_id} picturePath= {picturePath}/>
        </Box>

        <Box 
          flexBasis= {isNonMobileScreens ? "42%" : undefined}
          ml= {isNonMobileScreens ? undefined : "2rem"}
        >
          <MyPostWidget picturePath= {picturePath}/>
          <PostsWidget userId= {_id}/>
        </Box>

        {isNonMobileScreens && 
        <Box flexBasis= "26%" ></Box>}

      </Box>
    </Box>
  )
}

export default HomePage
