import React from 'react';
import { Box, useMediaQuery } from '@mui/material';
import { useEffect, useState } from 'react';
import { useSelector} from "react-redux";
import { useParams} from "react-router-dom";
import Navbar from "../component/Navbar";
import FriendListWidget from "../component/widgets/FriendListWidget";
import MyPostWidget from "../component/widgets/MyPostWidget";
import postsWidget from "../component/widgets/postsWidget";
import UserWidget from "../component/widgets/UserWidget";

const ProfilePage = () => {

const [user, setUser]= useState(null);
const{userId}= useParams();
const token= useSelector((state) => state.token );
const isNonMobileScreens= useMediaQuery("(min-width: 1000px");


const getUser= async () => {
  const res= await fetch(`http://localhost:3001/users/${userId}`,
  {
    method: "GET",
    headers: {Authorization: `Bearer ${token}`}
  })
  const data= await res.json();
  setUser(data);
}

useEffect(() => {
  getUser();
}, [])


if(!user) return null;



return

}

export default ProfilePage
